import { Component } from '@angular/core';
import { Text } from '../models/text';

@Component({
  selector: 'app-task3',
  templateUrl: './task3.component.html',
  styleUrls: ['./task3.component.css']
})
export class Task3Component {
  links:Text[]=[
    new Text("https://en.wikipedia.org/wiki/Bill_Gates"),
    new Text("https://www.gatesfoundation.org/"),
    new Text("https://www.gatesnotes.com/"),
    new Text("https://twitter.com/billgates")
  ]
}
<h2>Task 3</h2>

<ul>
    <li *ngFor="let link in li"><a href=""></a></li>

</ul>
