import { Component } from '@angular/core';
import { AppComponent } from '../app.component';
import { Quote } from '../models/quote';


@Component({
  selector: 'app-task2',
  templateUrl: './task2.component.html',
  styleUrls: ['./task2.component.css']
})
export class Task2Component extends AppComponent {
  quotes:Quote[]=[
  new Quote("If you think your teacher is tough, wait till you get a boss"),
  new Quote("Life is not fair — get used to it!"),
  new Quote("Success is a lousy teacher. It seduces smart people into thinking they can’t lose"),
  new Quote("Be nice to nerds. Chances are you’ll end up working for one")
    ]
}
